import React, { useState, useEffect } from "react";
import { Route, Routes, Link, BrowserRouter } from "react-router-dom";
import "./App.css";
import About from "./pages/About";
import Projects from "./pages/Projects";
import External from "./pages/External";

export default function App() {
  const [selectedDiv, setSelectedDiv] = useState(false);
  const [index, setIndex] = useState(null);
  const [isActive, setIsActive] = useState(false);



  const handleLinkClick = (i) => {
    setSelectedDiv(i);
    setIndex(i);
  };

  useEffect(() => {
    console.log(selectedDiv);
  }, [selectedDiv]);

  return (
    <BrowserRouter>
      <div className="terminal">
        <div className={`links ${isActive ? 'active' : ''}`}>
          <Link to="/">Home</Link>
          <Link to="/projects" onClick={() => handleLinkClick(1)}>
            Projects
          </Link>
          <Link to="/about"onClick={() => handleLinkClick(2)}>About</Link>
          <Link to="/external" onClick={() => handleLinkClick(3)}>External Links</Link>
        </div>
      </div>
      <Routes>
        <Route path="/" />
        <Route path="/Projects" element={<Projects />} />
        <Route path="/About" element={<About />} />
        <Route path="/External" element={<External />} />
      </Routes>
    </BrowserRouter>
  );
}
